using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Mvc.Rendering;
using Microsoft.EntityFrameworkCore;
using LINQ_Practice.Models;

namespace LINQ_Practice.Controllers
{
    public class LINQautoGeneratedController : Controller
    {
        private readonly DB2023_LINQContext _context;

        public LINQautoGeneratedController(DB2023_LINQContext context)
        {
            _context = context;
        }

        // GET: LINQautoGenerated
        public async Task<IActionResult> Index()
        {
            var dB2023_LINQContext = _context.TblEmployees.Include(t => t.DepartmentNavigation);
            return View(await dB2023_LINQContext.ToListAsync());
        }

        // GET: LINQautoGenerated/Details/5
        public async Task<IActionResult> Details(int? id)
        {
            if (id == null || _context.TblEmployees == null)
            {
                return NotFound();
            }

            var tblEmployee = await _context.TblEmployees
                .Include(t => t.DepartmentNavigation)
                .FirstOrDefaultAsync(m => m.Id == id);
            if (tblEmployee == null)
            {
                return NotFound();
            }

            return View(tblEmployee);
        }

        // GET: LINQautoGenerated/Create
        public IActionResult Create()
        {
            ViewData["Department"] = new SelectList(_context.TblDepartments, "Id", "Id");
            return View();
        }

        // POST: LINQautoGenerated/Create
        // To protect from overposting attacks, enable the specific properties you want to bind to.
